    private void btnsaitActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnsaitActionPerformed
        Telaprincipal tp = new Telaprincipal();
        this.dispose();
        tp.setVisible(true);
    }//GEN-LAST:event_btnsaitActionPerformed

    private void btnsalvarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnsalvarActionPerformed
       if(JOptionPane.showConfirmDialog(rootPane, "Deseja Realizar o Cadastro")== 0){
          //btnRealizarVenda.setText("Salvo");
          JOptionPane.showMessageDialog(rootPane, "Cadastro realizado com Sucesso!");
           Telaprincipal tp = new Telaprincipal();
           this.dispose();
            tp.setVisible(true);
       }else{
         // btnRealizarVenda.setText("NÃ£o Salvo");
          JOptionPane.showMessageDialog(rootPane, "Cadastro Cancelado ","Titulo",JOptionPane.INFORMATION_MESSAGE);
       }
    }//GEN-LAST:event_btnsalvarActionPerformed

